mapper.update:
update BASE_ROLE
set ROLE_CODE = #{roleCode,jdbcType=VARCHAR},
  ROLE_NAME = #{roleName,jdbcType=VARCHAR},
  MEMO = #{memo,jdbcType=VARCHAR}
where ROLE_ID = #{roleId,jdbcType=INTEGER}

mapper.select:
select ROLE_ID, ROLE_CODE, ROLE_NAME, MEMO
from BASE_ROLE
where ROLE_ID = #{roleId,jdbcType=INTEGER}
